func TestBadMode(t *testing.T) {
	_, err := marshallBpfInfoV1(bpfInfoV1{
		mode: 9999,
		fd:   1,
	})

	assert.Error(t, err)
}

func TestBadPinned(t *testing.T) {
	badPath := ""
	for i := len(badPath); i < xtBpfPathMax; i++ {
		badPath = badPath + "\x00"
	}
	_, err := MarshalBpfPinned(badPath + "\x00")

	assert.Error(t, err)
}

func TestBadBytecode(t *testing.T) {
	badFilter := ""
	// 28 loops plus one is 65 bpf instructions
	for i := 0; i < 28; i++ {
		badFilter = badFilter + fmt.Sprintf("host 192.0.2.%v or ", i)
	}
	_, err := MarshalBpfBytecode(badFilter + "host 192.0.2.64")

	assert.Error(t, err)
}

func TestBadFd(t *testing.T) {
	_, err := MarshalBpfFd(-1)

	assert.Error(t, err)
}

func TestBadBpfFilter(t *testing.T) {
	_, err := MarshalBpfBytecode("asdf")

	assert.Error(t, err)
}
